@Grab(group='commons-net', module='commons-net', version='2.0')
@Grab(group='com.jcraft', module='jsch', version='0.1.51')

import org.apache.commons.net.ftp.FTPSClient
import com.jcraft.jsch.*;

/////////////////////////////////////////////////////////////
/////////////////////////Configs/////////////////////////////
/////////////////////////////////////////////////////////////
String ftpdomain = "10.251.25.48"
int ftpport = 22
String ftpusername = "wlsadmin"
String ftppassword = "Ml0wls1"
String localdownloadlocation = localDir
String remotedownloadlocation = context.expand( '${#Project#props dir}' )+"ac.txt"

/////////////////////////////////////////////////////////////
/////////////////////////////////////////////////////////////


//Download da.txt
    try {
    	log.info("Starting sftp download process")
    	Session session = null;
    	log.info("Session defined")
        Channel channel = null;		
        JSch ssh = new JSch();

        log.info("Attempting to create session following parameters: "+ftpusername+", "+ftpdomain+", "+ftpport)
        session = ssh.getSession(ftpusername, ftpdomain, ftpport);
        log.info("Created session following parameters: "+ftpusername+", "+ftpdomain+", "+ftpport)
        session.setConfig("StrictHostKeyChecking", "no"); //auto accept secure host
        session.setPassword(ftppassword);

        session.setConfig("PreferredAuthentications", "publickey,keyboard-interactive,password");

        session.connect();
        log.info("Connected to session")

        channel = session.openChannel("sftp");
        channel.connect();
        log.info("Connected to channel")

        ChannelSftp sftp = (ChannelSftp) channel;
        sftp.get(remotedownloadlocation, localdownloadlocation);
        log.info("File Downloaded " + remotedownloadlocation + " TO " + localdownloadlocation)

    } catch (JSchException e) {
        e.printStackTrace();
        log.info("JSchException " + e.printStackTrace())
    } catch (SftpException e) {
        e.printStackTrace();
        log.info("SftpException " + e.printStackTrace())
    } finally {

        log.info("sftp downloadload process complete")
    }


