//Download da.txt
    try {
    	log.info("Starting sftp download process")
    	Session session = null;
    	log.info("Session defined")
        Channel channel = null;		
        JSch ssh = new JSch();

        log.info("Attempting to create session following parameters: "+ftpusername+", "+ftpdomain+", "+ftpport)
        session = ssh.getSession(ftpusername, ftpdomain, ftpport);
        log.info("Created session following parameters: "+ftpusername+", "+ftpdomain+", "+ftpport)
        session.setConfig("StrictHostKeyChecking", "no"); //auto accept secure host
